mapper.select:
SELECT tc.id,tc.car_no,tc.car_status,tc.station_id,ts.site_name from t_car_stock tc LEFT JOIN t_site ts ON ts.id = tc.station_id 
WHERE tc.station_id = #{siteId} and tc.car_status = 0 
limit #{page},#{pageSize}

SELECT id, site_name,site_address,longitude,latitude,province,city,area,street, site_status,create_time,tel,imgurl, (select disname from t_district where disid = t.province) provinceName, (select disname from t_district where disid = t.city) cityName, (select disname from t_district where disid = t.area) areaName, (select disname from t_district where disid = t.street) streetName FROM t_site t where 1=1
and site_name = #{siteName}
and site_status = #{siteStatus}
limit #{page},#{pageSize}

		SELECT count(*) FROM t_site t where 1=1
        
        	and site_name = #{siteName}
        
        
        	and site_status = #{siteStatus}
